import{_ as t,c as o,o as r,aj as a}from"./chunks/framework.D3ayZeS-.js";const g=JSON.parse('{"title":"Contribution Guide","description":"","frontmatter":{},"headers":[],"relativePath":"contributing.md","filePath":"contributing.md"}'),i={name:"contributing.md"};function n(l,e,s,c,d,u){return r(),o("div",null,[...e[0]||(e[0]=[a('<p><a href="./">Docs Index</a> | <a href="./../README.html">Project README</a></p><h1 id="contribution-guide" tabindex="-1">Contribution Guide <a class="header-anchor" href="#contribution-guide" aria-label="Permalink to &quot;Contribution Guide&quot;">​</a></h1><p>We welcome contributions from the community! To ensure a smooth and collaborative process, please follow these guidelines, which are derived from our internal handbook.</p><h2 id="core-principles" tabindex="-1">Core Principles <a class="header-anchor" href="#core-principles" aria-label="Permalink to &quot;Core Principles&quot;">​</a></h2><ul><li><strong>Quality Gates:</strong> Every contribution must pass type, lint, and test gates before merging.</li><li><strong>Documentation:</strong> Documentation and changelogs are first-class deliverables.</li><li><strong>Holistic Reviews:</strong> Code reviews ensure architectural and accessibility compliance, not just syntax.</li><li><strong>Continuous Standards:</strong> Security, performance, and accessibility are continuous concerns, not afterthoughts.</li></ul><h2 id="git-branching-model" tabindex="-1">Git Branching Model <a class="header-anchor" href="#git-branching-model" aria-label="Permalink to &quot;Git Branching Model&quot;">​</a></h2><p>We use a GitFlow-like branching model:</p><ul><li><code>main</code>: Protected branch for stable releases.</li><li><code>develop</code>: Integration branch for new features.</li><li><code>feature/*</code>: For developing new features.</li><li><code>fix/*</code>: For minor bug fixes.</li><li><code>docs/*</code>: For documentation updates.</li></ul><h3 id="merge-flow" tabindex="-1">Merge Flow <a class="header-anchor" href="#merge-flow" aria-label="Permalink to &quot;Merge Flow&quot;">​</a></h3><ol><li>Create your <code>feature/*</code> or <code>fix/*</code> branch from <code>develop</code>.</li><li>Once complete, open a Pull Request (PR) to merge back into <code>develop</code>.</li><li>After passing CI and code review, your PR will be merged.</li><li>Periodically, <code>develop</code> is merged into <code>main</code> to create a new release.</li></ol><h2 id="code-review-process" tabindex="-1">Code Review Process <a class="header-anchor" href="#code-review-process" aria-label="Permalink to &quot;Code Review Process&quot;">​</a></h2><ul><li><strong>Approvals:</strong> At least two approvals are required (one technical, one QA/UX).</li><li><strong>CI Checks:</strong> All CI checks must be green before a PR can be merged.</li><li><strong>Issue Linking:</strong> Each PR should link to a tracked issue.</li><li><strong>Documentation:</strong> If your PR changes an API, the documentation must be updated accordingly.</li></ul><h3 id="reviewer-focus-areas" tabindex="-1">Reviewer Focus Areas <a class="header-anchor" href="#reviewer-focus-areas" aria-label="Permalink to &quot;Reviewer Focus Areas&quot;">​</a></h3><table tabindex="0"><thead><tr><th>Reviewer</th><th>Focus Area</th></tr></thead><tbody><tr><td>Architecture Lead</td><td>Core logic, store, API, DCE</td></tr><tr><td>UX Engineer</td><td>Components, design tokens, accessibility</td></tr><tr><td>Infrastructure</td><td>Build, CI/CD, performance reports</td></tr><tr><td>QA Lead</td><td>Tests, coverage, regression results</td></tr></tbody></table><hr><p>Prev: <a href="./security.html">Security</a> | Next: <a href="./deployment.html">Deployment</a></p><p>See also: <a href="./">Docs Index</a> | <a href="./../README.html">Project README</a></p>',17)])])}const m=t(i,[["render",n]]);export{g as __pageData,m as default};
